and a.add_time <= #{endTime}
and a.issue_subject like (concat(#{issueSubject},'%'))
AND a.issue_number like ( SELECT concat(replace(LEFT (c.ORG_NO_, 9),'000',''),'%') AS org_no FROM `act_id_user` c WHERE c.id_ = #{userId})

