mapper.select:
select count(1) from t_main_issue_kh


        and qhzd_id = #{qhzdId}


        and khyf = #{khyf}


        and bm_type = #{bmType}

select
t.id,t.qhzd_id,t.qhzd_name,ifnull(t.zxcl,0)zxcl,ifnull(t.ajbz,0)ajbz,ifnull(t.ascl,0)ascl,ifnull(t.kscl,0)kscl,
ifnull(t.wzxgfx,0)wzxgfx,ifnull(t.ffts,0)ffts,ifnull(t.dbwz,0)dbwz,ifnull(t.gzxt,0)gzxt,ifnull(t.jdjc,0)jdjc,
ifnull(t.xcjz,0)xcjz,ifnull(t.xcbd,0)xcbd,t.khyf,t.khsj,t.bm_type from t_main_issue_kh t



        and t.qhzd_id = #{qhzdId}


        and t.khyf = #{khyf}


        and t.bm_type = #{bmType}




    order by t.khyf desc


    limit #{pageIndex},#{pageSize}

select g.id_ as qhzd_id ,g.name_ as qhzd_name,t1.zxcl,t1.ajbz, t1.ascl,t1.kscl,t1.wzxgfx,t1.ffts, t1.dbwz,t1.gzxt,t1.sljb,t1.xcjz,t1.xcbd,t1.jdjc from (select * from act_id_group where pid_ is null and type_=#{bmType})g left join ( select t.qhzd_id,t.qhzd_name,FORMAT(avg(t.zxcl),2)as zxcl,FORMAT(avg(t.ajbz),2) as ajbz, FORMAT(avg(t.ascl),2) as ascl,FORMAT(avg(t.kscl),2) as kscl,FORMAT(avg(t.wzxgfx),2)as wzxgfx,FORMAT(avg(t.ffts),2)as ffts, FORMAT(avg(t.dbwz),2)as dbwz,FORMAT(avg(t.gzxt),2)as gzxt,FORMAT(avg(t.sljb),2) as sljb,FORMAT(avg(t.xcjz),2) as xcjz, FORMAT(avg(IFNULL(t.xcbd,0)),2)as xcbd,FORMAT(avg(t.jdjc),2) as jdjc from t_main_issue_kh t
and t.bm_type = #{bmType}
and t.khyf like '${khyf}%'
GROUP BY t.qhzd_id )t1 on g.ID_=t1.qhzd_id
limit #{pageIndex},#{pageSize}

select g.id_ as qhzd_id ,g.name_ as qhzd_name,t1.zxcl,t1.ajbz, t1.ascl,t1.kscl,t1.wzxgfx,t1.ffts, t1.dbwz,t1.gzxt,t1.sljb,t1.xcjz,t1.xcbd,t1.jdjc from (select * from act_id_group where pid_ is null and type_=#{bmType})g left join ( select t.qhzd_id,t.qhzd_name,FORMAT(avg(t.zxcl),2)as zxcl,FORMAT(avg(t.ajbz),2) as ajbz, FORMAT(avg(t.ascl),2) as ascl,FORMAT(avg(t.kscl),2) as kscl,FORMAT(avg(t.wzxgfx),2)as wzxgfx,FORMAT(avg(t.ffts),2)as ffts, FORMAT(avg(t.dbwz),2)as dbwz,FORMAT(avg(t.gzxt),2)as gzxt,FORMAT(avg(t.sljb),2) as sljb,FORMAT(avg(t.xcjz),2) as xcjz,FORMAT(avg(IFNULL(t.xcbd,0)),2)as xcbd, FORMAT(avg(t.jdjc),2) as jdjc from t_main_issue_kh t
and t.bm_type = #{bmType}
and t.khyf BETWEEN #{start} and #{end}
GROUP BY t.qhzd_id )t1 on g.ID_=t1.qhzd_id
limit #{pageIndex},#{pageSize}

select count(t2.id_)from (select * from (select * from act_id_group where pid_ is null and type_=#{bmType})g left join ( select t.qhzd_id,t.qhzd_name,FORMAT(avg(t.zxcl),2)as zxcl,FORMAT(avg(t.ajbz),2) as ajbz, FORMAT(avg(t.ascl),2) as ascl,FORMAT(avg(t.kscl),2) as kscl,FORMAT(avg(t.wzxgfx),2)as wzxgfx,FORMAT(avg(t.ffts),2)as ffts, FORMAT(avg(t.dbwz),2)as dbwz,FORMAT(avg(t.gzxt),2)as gzxt,FORMAT(avg(t.sljb),2) as sljb,FORMAT(avg(t.xcjz),2) as xcjz,FORMAT(avg(t.xcbd),2)as xcbd, FORMAT(avg(t.jdjc),2) as jdjc from t_main_issue_kh t
and t.bm_type = #{bmType}
and t.khyf BETWEEN #{start} and #{end}
GROUP BY t.qhzd_id )t1 on g.ID_=t1.qhzd_id)t2

